import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    saveCard: false
  });

  const [message, setMessage] = useState(''); // State for message

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/payments', formData)
      .then(response => {
        console.log('Form data submitted', response.data);
        setMessage('Payment was successful! Thank you for your purchase.');
        setFormData({
          name: '',
          email: '',
          cardNumber: '',
          expiryDate: '',
          cvv: '',
          saveCard: false
        });
      })
      .catch(error => {
        console.error('There was an error!', error);
        setMessage('Payment failed. Please try again or contact support.');
      });
  };

  return (
    <div className="form-container">
      <img src="/logo.png" alt="Logo" className="logo" width="160px" />
      <h1>Enter Payment Details</h1>
      <form onSubmit={handleSubmit}>
        <label>Name on card:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />

        <label>Email Address:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required />

        <label>Card Number:</label>
        <input type="text" name="cardNumber" value={formData.cardNumber} onChange={handleChange} required />

        <div className="inline-fields">
          <div>
            <label>Expiry Date:</label>
            <input type="date" name="expiryDate" value={formData.expiryDate} onChange={handleChange} required />
          </div>
          <div>
            <label>CVV:</label>
            <input type="text" name="cvv" value={formData.cvv} onChange={handleChange} required />
          </div>
        </div>

        <div className="checkbox-container">
          <input type="checkbox" name="saveCard" checked={formData.saveCard} onChange={handleChange} />
          <label htmlFor="saveCard">Save this Card for later payments</label>
        </div>

        <button type="submit">Pay Now</button>
      </form>
      {message && <div className="message">{message}</div>} {/* Display the message */}
    </div>
  );
}

export default App;
